mapper.select:
SELECT id
FROM register_material
WHERE maintain_id in

	#{item}


	AND year(create_time) = #{year}


	AND month(create_time) = #{month}

order by create_time DESC

SELECT register_material.id as id, pro_material.id as maintain_id,pro_material.name as maintain_name, com_producer.id as maintainer_id,com_producer.name as maintainer_name, com_supplier.id as supplier_id,com_supplier.name as supplier_name, user.id as user_id,user.name as user_name, register_material.price,register_material.count, register_material.total_price,register_material.remark, register_material.create_time,register_material.status FROM register_material,user,pro_material,com_producer,com_supplier WHERE register_material.id in
#{item}
AND register_material.material_id = pro_material.id AND register_material.producer_id = com_producer.id AND register_material.supplier_id = com_supplier.id AND register_material.user_id = user.id

SELECT sum(total_price)
FROM register_material,pro_material
WHERE plat_id = #{platId}
AND pro_material.id = register_material.id

	AND year(create_time) = #{year}


	AND month(create_time) = #{month}
